


import math
import numbers
from contextlib import suppress


def is_scalar_nan(x):
    
    return (
        not isinstance(x, numbers.Integral)
        and isinstance(x, numbers.Real)
        and math.isnan(x)
    )


def is_pandas_na(x):
    
    with suppress(ImportError):
        from pandas import NA

        return x is NA

    return False
